import java.util.Scanner;


public class e5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner cin = new Scanner(System.in);
		int a=cin.nextInt();
		int b=cin.nextInt();
		int c=cin.nextInt();
		double d=0;
		d=(a+b+c)/3.0;
		System.out.println(String.format("%.3f", d));

	}

}
