
public class split {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str=new String("safasdfsdfdjkfdsyifuasd");
		String[] sp=str.split("");
		for(int i=0;i<sp.length;i++)
			{System.out.printf("sp[%d]",i);
		System.out.print(sp[i]);
		System.out.println();}
		
		
	}

}
